// Generated by Copilot
const db = require('./db/connection');
const { generateText } = require('./services/openaiService');

async function main() {
  try {
    console.log('Starting application...');
    // Obtener un prompt de la base de datos
    const res = await db.query('SELECT prompt FROM prompts LIMIT 1');
    const prompt = res.rows[0]?.prompt || 'Hola mundo';

    // Llamar al servicio externo
    const respuesta = await generateText(prompt);

    // Mostrar el resultado
    console.log('Respuesta generada:', respuesta);
  } catch (error) {
    console.error('Error:', error);
  } finally {
    await db.end();
  }
}

main().catch(console.error);

