// generated by ChatGPT
export function getCombinations(array) {
  const result = [];
  for (let i = 0; i < array.length; i++) {
    for (let j = i + 1; j < array.length; j++) {
      result.push([array[i], array[j]]);
    }
  }
  return result;
}

export async function checkColorContrast(hexCodes) {
  console.log("executing checkColorContrast");
  const result = [];
  for (let i = 0; i < hexCodes.length - 1; i++) {
    for (let j = i + 1; j < hexCodes.length; j++) {
      const fcolor = hexCodes[i].replace("#", "");
      const bcolor = hexCodes[j].replace("#", "");
      const apiUrl = `https://webaim.org/resources/contrastchecker/?fcolor=${fcolor}&bcolor=${bcolor}&api`;

      const response = await fetch(apiUrl);
      const data = await response.json();
      // const contrastRatio = parseFloat(data.match(/Ratio: (\d+\.\d+)/)[1]);

      result.push({
        colors: [hexCodes[i], hexCodes[j]],
        data: data,
      });
    }
  }
  return result;
}
