import bcrypt from 'bcrypt';
import jwt from 'jsonwebtoken';
import { supabase } from '../supabaseClient.js';


//generated by cursor
export const login = async (req, res) => {
    const { username, password } = req.body;
    if (!username || !password) {
        return res.status(400).json({ error: 'Username and password are required' });
    }
    // Fetch user from Supabase
    const { data: users, error } = await supabase
        .from('users')
        .select('*')
        .eq('username', username)
        .limit(1);
    if (error) {
        return res.status(500).json({ error: 'Database error', details: error.message });
    }
    if (!users || users.length === 0) {
        return res.status(401).json({ error: 'Invalid username or password' });
    }
    const user = users[0];
    // Compare password
    const match = await bcrypt.compare(password, user.password_hash);
    if (!match) {
        return res.status(401).json({ error: 'Invalid username or password' });
    }
    // Generate JWT
    const token = jwt.sign({ id: user.id, username: user.username }, process.env.JWT_SECRET, { expiresIn: '1h' });
    return res.json({ token });
};

export const register = async (req, res) => {
    const { username, password } = req.body;
    if (!username || !password) {
        return res.status(400).json({ error: 'Username and password are required' });
    }
    // Check if user already exists
    const { data: users, error: fetchError } = await supabase
        .from('users')
        .select('id')
        .eq('username', username)
        .limit(1);
    if (fetchError) {
        return res.status(500).json({ error: 'Database error', details: fetchError.message });
    }
    if (users && users.length > 0) {
        return res.status(409).json({ error: 'Username already exists' });
    }
    // Hash password
    const password_hash = await bcrypt.hash(password, 10);
    // Insert new user
    const { data: newUser, error: insertError } = await supabase
        .from('users')
        .insert([{ username, password_hash }])
        .select()
        .single();
    if (insertError) {
        return res.status(500).json({ error: 'Database error', details: insertError.message });
    }
    // Generate JWT
    const token = jwt.sign({ id: newUser.id, username: newUser.username }, process.env.JWT_SECRET, { expiresIn: '1h' });
    return res.status(201).json({ token });
};